class DBHelper{static get DATABASE_URL(){return"http://localhost:1337/restaurants"}static get RESTAURANT_REVIEW_URL(){return"http://localhost:1337/reviews"}static getChangeFavoriteURL(e,t){return`http://localhost:1337/restaurants/${e}/?is_favorite=${t}`}static fetchRestaurants(e){cargarRestaurantes().then(function(t){if(t.length>0)e(null,t);else{fetch(DBHelper.DATABASE_URL).then(function(e){return e.json()}).then(function(t){t.forEach(e=>{console.log("Creando restaurante en la bd..."+e.id),crearRestaurante(e)}),e(null,t)})}})}static fetchReviews(e){cargarReviews().then(function(t){if(t.length>0)e(null,t);else{fetch(DBHelper.RESTAURANT_REVIEW_URL).then(function(e){return e.json()}).then(function(t){console.log(t),t.forEach(e=>{console.log("Creando review en la bd..."+e.id),e.pending="false",crearReview(e)}),e(null,t)})}})}static fetchRestaurantById(e,t){DBHelper.fetchRestaurants((n,a)=>{if(n)console.log("error consultando el restaurante"),t(n,null);else{const n=a.find(t=>t.id==e);n?t(null,n):t("Restaurant does not exist",null)}})}static fetchRestaurantByCuisine(e,t){DBHelper.fetchRestaurants((n,a)=>{if(n)t(n,null);else{const n=a.filter(t=>t.cuisine_type==e);t(null,n)}})}static fetchRestaurantByNeighborhood(e,t){DBHelper.fetchRestaurants((n,a)=>{if(n)t(n,null);else{const n=a.filter(t=>t.neighborhood==e);t(null,n)}})}static fetchRestaurantByCuisineAndNeighborhood(e,t,n){DBHelper.fetchRestaurants((a,r)=>{if(a)n(a,null);else{let a=r;"all"!=e&&(a=a.filter(t=>t.cuisine_type==e)),"all"!=t&&(a=a.filter(e=>e.neighborhood==t)),n(null,a)}})}static fetchNeighborhoods(e){DBHelper.fetchRestaurants((t,n)=>{if(t)e(t,null);else{const t=n.map((e,t)=>n[t].neighborhood),a=t.filter((e,n)=>t.indexOf(e)==n);e(null,a)}})}static fetchCuisines(e){DBHelper.fetchRestaurants((t,n)=>{if(t)e(t,null);else{const t=n.map((e,t)=>n[t].cuisine_type),a=t.filter((e,n)=>t.indexOf(e)==n);e(null,a)}})}static fetchReviewsByRestaurant(e,t){DBHelper.fetchReviews((n,a)=>{if(n)console.log("error consultando las reviews del restaurante: "+e),t(n,null);else{const n=a.filter(t=>t.restaurant_id==e);n?t(null,n):t("No se encontraron reviews",null)}})}static postReview(e,t){fetch(this.RESTAURANT_REVIEW_URL,{method:"post",body:{restaurant_id:e.restaurant_id,name:e.name,rating:e.rating,comments:e.comments}}).then(function(e){return"Created"==e.statusText}).then(function(n){e.createdAt=e.id,n||(e.pending="true"),crearReview(e),t(null,e)}).catch(function(e){let n="Error ejecutando la petición: "+e;console.log(n),t(n,null)})}static sincroPendingReviews(){cargarReviewsPendientes().then(function(e){e.forEach(e=>{fetch(DBHelper.RESTAURANT_REVIEW_URL,{method:"post",body:{restaurant_id:e.restaurant_id,name:e.name,rating:e.rating,comments:e.comments}}).then(function(t){return"Created"==t.statusText&&(e.pending="false",updatePendingReview(e),!0)})})})}static changeFavoriteStatus(e,t){fetch(this.getChangeFavoriteURL(e.id,e.is_favorite),{method:"put"}).then(function(e){e.ok?t(null,!0):t(null,!1)}).catch(function(e){let n="Error ejecutando la petición: "+e;console.log(n),t(n,null)})}static urlForRestaurant(e){return`./restaurant.html?id=${e.id}`}static imageUrlForRestaurant(e){return void 0===e?null:`img/${e.photograph}.jpg`}static mapMarkerForRestaurant(e,t){if(google){return new google.maps.Marker({position:e.latlng,title:e.name,url:DBHelper.urlForRestaurant(e),map:t,animation:google.maps.Animation.DROP})}return null}}